) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=latin1 COMMENT='delivery status table';






CREATE TABLE `invoice_details` (
  `invoice_id` int(7) NOT NULL AUTO_INCREMENT,
